function typewriter
{
    text="$1"
    delay="$2"

    for i in $(seq 0 $(expr length "${text}")) ; do
        echo -e -n "${text:$i:1}"
        sleep ${delay}
    done
}

function typewriter_red
{
    text="$1"
    delay="$2"

    for i in $(seq 0 $(expr length "${text}")) ; do
        echo -e -n "${RED}${text:$i:1}${RESET}"
        sleep ${delay}
    done
}

